  const [cart,setCart] = state.userAPI.data
  
  function add_to_cart(val){
    console.log(val._id)
    for (let i = 0; i < cart.length; i++)
    {

      if(i._id == val._id)
      {
         alert("already in the cart")
      }else{

      }
    }
    cart.push(val)
    setCart(cart)
    console.log(cart.length)
  }
  return (
<div className='row     p-3  m-2 text-center  '>

{
  data.map(val =>
    <div key={val._id} 
