// @flow strict

import React, { useState } from 'react'
//import data_list from '../itemlist.js';
import logo from '../img/logo.png';
import axios from 'axios';





async function Home() {
  const [data,setdata] = useState([])
  useEffect(() =>{
        if(token){
            const getUser = async () =>{
                try {
                        const res = await axios.get('/shopowner/info', {    headers: {Authorization: token}})
                         setname(res.data.user.name)
                         setlastname(res.data.user.lastname)
                         setEmail(res.data.user.email)
                         setphone(res.data.user.phone)
                         setshopname(res.data.user.shopname)
                         setzip(res.data.user.zip)
                         setshopadd(res.data.user.shopadd)
                         setadd1(res.data.user.add1)
                         setadd2(res.data.user.add2)
                         setadd3(res.data.user.add3)
                         setifsc(res.data.user.ifsc)
                         setbank(res.data.user.bank)
                         setgst(res.data.user.gst)
                         setage(res.data.user.age)


                         try {
                            const res1 = await axios.get('/shopowner/all', {    headers: {Authorization: token}})

                            setData(res1.data.list)
                    } catch (err) {
                        alert(err.response.data.msg)
                    }
                } catch (err) {
                  
                    alert(err.response.data.msg)
                }

            }
            getUser()
            
        }
    },[token])
    return (

<div bg-Primary>
          
          <Calender data_list={res.data.list}/>          
                  </div>      
    );
};


 const  Calender=(data_list)=> {

  const [data,setData] = useState(data_list);
  
  function handle_item(id){
    alert(id);
  }
  function remove(id){
    const newlist = data.filter((i)=>i.id !== id);
    setData(newlist);
  }
  return (



<div className='row     p-3  m-2 text-center '>

{
  data.map(val =>
    
    <div key={val.id}  onClick={()=>{handle_item(val.id)
    }} className='  hover_box container-fluid   border  col-sm-10 col-md-4 col-lg-3   p-2 mt-3 text-center' >
    <div className=' container-fluid'>
        <div>
            <img src={val.img} width="250px" height="250px"/>
            </div>
        
      <h6>
        {
          val.name
        }
      </h6>
      <p>
        
         â‚¹{
          val.Price
        } 
      </p>

    </div>
    <h4>
      {val.about}
    </h4>
  
    <div>
      <button className='hover_box btn btn-outline-warning'  onClick={()=>(val.id)}>
                 <a className='nav-link' href='/CustomerLogin'><h6> Add to Cart</h6></a>

      </button>
      </div>
</div>
  )
}

</div>

 )
}
export default Home;
