import * as React from 'react';
import { BrowserRouter, Link, Route, Router, Routes } from 'react-router-dom';
import logo from '../../img/logo.png'
function Location(){

    // var x = document.getElementById("demo");
    const [x,setx]= React.useState(0)
    const [y,sety]= React.useState(0)
    const [url ,seturl] = React.useState('')
    function getLocation() {

        if (navigator.geolocation) {
          navigator.geolocation.watchPosition(showPosition);
        } else { 
          x.innerHTML = "Geolocation is not supported by this browser.";
        }
      }
          
      function showPosition(position) {
          // x.innerHTML="Latitude: " + position.coords.latitude + 
          // "<br>Longitude: " + position.coords.longitude;
          // <img width="8000" height="400" src="https://maps.geoapify.com/v1/tile/carto/{7.2}/"{x}+"/"+{y}".png?&apiKey=38cb046d4a9d4455a30347cd9b59845e"/>
          const x1=position.coords.latitude;
          const  y1=position.coords.longitude;
          setx(x1)
          sety(y1)
          const k = "https://maps.geoapify.com/v1/staticmap?style=osm-carto&width=600&height=400&center=lonlat:"+{x1}+","+{y1}+"&zoom=12.877&apiKey=38cb046d4a9d4455a30347cd9b59845e"
          console.log(k)
          seturl(k)
      }


    return(
        <>
            <div className='text-center '>
              {x}

            <button className="Location-button btn btn-outline-dark  " onClick={getLocation}>Click To give the Location</button>
            <br/>
            <img width="600" height="400" src ={url} alt="1427 South Henderson Street, Seattle, WA 98108, United States of America"/>

            <div>
            <p id="demo"></p>
            </div>
            </div>
        </>
    )
}
export default Location;
