// @flow strict

import React, { useState ,useContext} from 'react'
import logo from '../../img/logo.png';
import cart from '../client/Cart';

import { GlobalState } from './Global.js';
 const  Home=()=> {

  
  const state = useContext(GlobalState)
  const [order,setorder] = state.userAPI.order
  function add_to_cart(id,name){
    const cart = order.filter((i)=>i.id === id);
    console.log(cart);
  }

  
  function handle_item(id,name){
    alert(id + "  "+ name);
  }



  function remove(id){
    const newlist = order.filter((i)=>i.id !== id);
    setorder(newlist);
  }
  return (
    
<div className='row     p-3  m-2 text-center  '>

{
  order.map(val =>
    <div key={val.product._id}  onClick={()=>{handle_item(val.product._id,val.product.name)
    }}
     className='  hover_box container-fluid   border    p-1 mt-5  text-center' >
    <div className=' container-fluid'>
       
     
    
      
      
      

    </div>
    

    
</div>
  )
}

</div>

 )
}
export default Home;
