// @flow strict
import React from 'react'
import { useState,useContext } from 'react';
import { GlobalState } from './Global';



function Home() {

  const state = useContext(GlobalState)
  const [cart,setCart]  = state.userAPI.cart
  const update = state.userAPI.update
  console.log(cart)
  
    const [sum,SetSum] = useState(0);


  
  function handle_item(id,name){
    alert(id + "  "+ name);
  }

function add(id)
{
   cart.map((i)=>{
    if(i.id === id)
    {
      i.size = i.size+ 1;
    }
  })
  setCart(cart)
  update()
  cal_sum(cart);

}

function miuns(id)
{
   cart.map((i)=>{
    if(i.id === id)
    {
      if(i.size >0)
      {
      i.size = i.size- 1;
      }
    }
  })
  const newlist = cart.filter((i)=> i.id );
  setCart(newlist)
  update()
  cal_sum(newlist);

}



function cal_sum(newlist)
{

  var new_sum =0
  cart.map((i)=>{
    new_sum = new_sum+i.size*i.prize;
  }) 
  SetSum(new_sum);
}
  function remove(id){
    const newlist = cart.filter((i)=>i.id !== id);
    setCart(newlist);
    update()
    cal_sum(newlist);
  }
  return (
<>
<div className='p-5'>

</div>

    <div>
      <h1 className='bg-primary p-5 text-center'>
        sum = {sum}
        <br/>
        Date of Delivary: {Date()}
      </h1>
    </div>
<div className='row     p-3  m-2 text-center  '>
{
  cart.map(val =>
      
    <div key={val.id}  
     className='  hover_box container-fluid   border  row  p-1 mt-5  text-center' >
    <div className=' row container-fluid'>
        <div className='col-sm-10 col-md-5 col-lg-5 '>
            <img src={val.url} width="250px" height="250px"/>
        </div>
        <div className='col-sm-10 col-md-5 col-lg-5 '>
        <h1
        >
        {
          val.name
        }
      </h1>
      <h3>
        
         â‚¹{
          val.prize
        } 
      </h3>


      <div class="btn-group" role="group" aria-label="Basic example">
         <button type="button" onClick={()=>miuns(val.id)} class="btn btn-secondary"> <h1> - </h1></button>
         <button type="button" class="btn btn-secondary"><h1> {val.size} </h1></button>
         <button type="button" onClick={()=>add(val.id)} class="btn btn-secondary "><h1> + </h1></button>
         <button className='hover_box btn bg-danger p-3  ' onClick={()=>remove(val.id)}>
                 <h4 ><svg xmlns="http://www.w3.org/2000/svg" width="40" height="30" fill="currentColor" class="bi bi-trash" viewBox="0 0 16 16">
  <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z"/>
  <path fill-rule="evenodd" d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"/>
</svg>   </h4>
              </button>
      </div>
      <div className='p-3'/>
      <div>
      </div>
          </div>
    </div>
    <div>
      </div>
</div>
    
   
  )
}

</div>

 </>)
}
export default Home;
