import {useState, useEffect} from 'react'
import axios from 'axios'

function UserAPI(token) {
   const [data,setData] = useState([]);
   const [name,setname]=useState('')
   const [lastname,setlastname]=useState('')
   const [email,setEmail]=useState('')
   const [phone,setphone]=useState(0)
   const [zip,setzip]=useState(0)
  const [age,setage] = useState('')
  const [add,setadd] = useState('')
    useEffect(() =>{
        if(token){
            const getUser = async () =>{
                try {
                        const res = await axios.get('/customer/info', {    headers: {Authorization: token}})
                         setname(res.data.user.name)
                         setlastname(res.data.user.lastname)
                         setEmail(res.data.user.email)
                         setphone(res.data.user.phone)
                         setzip(res.data.user.zip)
                         setadd(res.data.user.add)
                         setage(res.data.user.age)


                         try {
                           // const res1 = await axios.get('/shopowner/all', {    headers: {Authorization: token}})

                            //setData(res1.data.list)
                    } catch (err) {
                        alert(err.response.data.msg)
                    }
                } catch (err) {
                  
                    alert(err.response.data.msg)
                }

            }
            getUser()
            
        }
    },[token])
    return {
        name:[name,setname],
        lastname:[lastname,setlastname],
        phone:[phone,setphone],
        email:[email,setEmail],
        data:[data,setData],
        zip:[zip,setzip],
        add:[add,setadd],
        age:[age,setage]
    }
}

export default UserAPI
