import {useState, useEffect} from 'react'
import axios from 'axios'

function UserAPI(token) {
   const [products,setproducts] = useState([]);
   const [pooja,setpooja] = useState([]);

    useEffect(() =>{
        if(token){
            const getUser = async () =>{
                try {
                        const res = await axios.get('/shopowner/info', {    headers: {Authorization: token}})
                        setproducts(res.data.products)
                        setpooja(res.data.pooja)

                         try {
                            const res1 = await axios.get('/shopowner/all', {    headers: {Authorization: token}})

                            setData(res1.data.list)
                    } catch (err) {
                        alert(err.response.data.msg)
                    }
                } catch (err) {
                  
                    alert(err.response.data.msg)
                }

            }
            getUser()
            
        }
    },[token])
    return {
        name:[name,setname],
        lastname:[lastname,setlastname],
        phone:[phone,setphone],
        email:[email,setEmail],
        data:[data,setData],
        shopname:[shopname,setshopname],
        shopadd:[shopadd,setshopadd],
        zip:[zip,setzip],
        gst:[gst,setgst],
        bank:[bank,setbank],
        ifsc:[ifsc,setifsc],
        add1:[add1,setadd1],
        add2:[add2,setadd2],
        add3:[add3,setadd3],
        age:[age,setage],


    }
}

export default UserAPI
