// @flow strict

import React, { useState } from 'react'
import data_list from '../../itemlist.js';
import logo from '../../img/logo.png';
import cart from './Cart';

import { GlobalState } from './Global'
import { useContext } from 'react'



function Home() {
  const state = useContext(GlobalState)
  const [cart,setcart] = state.userAPI.cart
  const addcart = state.userAPI.addcart


   

  const [data,setData] = useState(data_list);
  
  function add_to_cart(id,name){
    const k = {"id":id,"q":1}
    cart.push(k)
    setcart(cart)
    console.log(cart)
    addcart(cart)

  }

  function handle_item(id,name){
    alert(id + "  "+ name);
  }



  function remove(id){
    const newlist = data.filter((i)=>i.id !== id);
    setData(newlist);
  }
  return (



<div className='row     p-3  m-2 text-center  '>

{
  data.map(val =>
    
    <div key={val.id}  onClick={()=>{handle_item(val.id,val.name)
    }}
     className='  hover_box container-fluid   border  col-sm-10 col-md-4 col-lg-3   p-1 mt-5  text-center' >
    <div className=' container-fluid'>
        <div> 
            <img src={val.img} width="250px" height="250px"/>
            </div>
        
      <h6>
        {
          val.name
        }
      </h6>
      <p>
        
         â‚¹{
          val.Price
        } 
      </p>

    </div>
    <h4>
      {val.about}
    </h4>

    <div>
      <button className='hover_box btn btn-outline-warning'>
                 <h4 onClick={()=> add_to_cart(val.id,val.name)}> Add to Cart      </h4>
      </button>
      </div>
</div>
  )
}

</div>

 )
}
export default Home;
